package main

import "fmt"

/**
字符串中的第一个唯一字符

给定一个字符串 s ，找到 它的第一个不重复的字符，并返回它的索引 。如果不存在，则返回 -1 。

输入: s = "loveleetcode"
输出: 2
*/
func firstUniqChar2(s string) int {
	chars := []byte(s)
	count := make([]int, 26, 26)
	length := len(chars)

	for i := 0; i < length; i++ {
		count[chars[i]-'a']++
	}

	for i := 0; i < length; i++ {
		v := chars[i]
		if count[v-'a'] == 1 {
			return i
		}
	}
	return -1
}

func firstUniqChar(s string) int {
	chars := []byte(s)
	length := len(chars)

	for i := 0; i < length; i++ {
		v := chars[i]

		flag := false
		for j := 0; j < length; j++ {
			if i != j && chars[j] == v {
				flag = true
				break
			}
		}

		if !flag {
			return i
		}
	}
	return -1
}

func main() {
	s := ""
	ret := firstUniqChar2(s)

	fmt.Println(ret)
}
